$(document).ready(function(){$("body").on("click",".scroolpage",function(o){o.preventDefault(),$(this).each(function(){var o=$(this).data("hash");$("html, body").delay(500).animate({scrollTop:$("#"+o).offset().top},500)})}),$('[data-toggle="tooltip"]').tooltip(),$(".navbar a, footer a[href='#myPage']").on("click",function(o){if(""!==this.hash){o.preventDefault();var t=this.hash;$("html, body").animate({scrollTop:$(t).offset().top},900,function(){window.location.hash=t})}})});